from django.db import models
from django.utils.safestring import mark_safe

Active = 1
Inactive = 2
# Create your models here.
status_choice = (
    (Active, "Active" ),
    (Inactive , 'Inactive'),
)


class About(models.Model):
    title = models.CharField(max_length=100,blank=True)
    description = models.TextField(max_length=1000,blank = True)
    image = models.FileField(upload_to='about_us',blank=True)
    image_title = models.CharField(max_length=200,blank = True)
    is_active = models.BooleanField(default=True)
    status = models.IntegerField(choices=status_choice,blank = True,default=1)

    class Meta:
        verbose_name_plural  = 'About'



    def image_view(self):
        return mark_safe(f'<img src = "{self.image.url}" width = "40"/>')

    

    
    def status_and_logo(self):
        if self.status == 1:
              return mark_safe(f'<img src="data:image/jpeg;base64," width="15" height="15" style /><span style="color:green ; font-weight : 600;">Active</span>')

        else:
            return mark_safe(f'<img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRBoPn7gWa276XisC5y6bs3l73xhawzbo_Huw&usqp=CAU" width="22" height="15" style="color:red" /><span style="color:red">Inactive</span>')

    status_and_logo.short_description = 'Status'
    status_and_logo.allow_tags = True


class what_we_do(models.Model):
    image = models.FileField(upload_to='What_we_do/about_us',blank=True)
    title = models.CharField(max_length=100,blank=True)
    description = models.TextField(max_length=1000,blank = True)
    status = models.IntegerField(choices=status_choice,blank = True,default=1)

    class Meta:
        verbose_name_plural = 'What we do'



    def image_view(self):
        if self.image:
            return mark_safe(f'<img src = "{self.image.url}" width = "40"/>')
        return ""


    
    def status_and_logo(self):
        if self.status == 1:
              return mark_safe(f'<img src="data:image/jpeg;base64," width="15" height="15" style /><span style="color:green ; font-weight : 600;">Active</span>')

        else:
            return mark_safe(f'<img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRBoPn7gWa276XisC5y6bs3l73xhawzbo_Huw&usqp=CAU" width="22" height="15" style="color:red" /><span style="color:red">Inactive</span>')

    status_and_logo.short_description = 'Status'
    status_and_logo.allow_tags = True




    



    
